
class ServiceFaultException(Exception):

    def __init__(self, statuscode, message):
        self.xml_namespace_uri = "http://docs.openstack.org/loadbalancers/api/v1.0"
        self.statuscode = statuscode
        self.message = message


    def get_statuscode(self):
        return self.statuscode
    

    def get_as_string(self, format):

        if format == "xml":
            return self.get_as_xml()
        else:
            return self.get_as_json()



