from openstack.common.ServiceFaultException import ServiceFaultException

class ServiceUnavailableException(ServiceFaultException):

    def __init__(self, message):
        ServiceFaultException.__init__(self, 500, message)


    def get_as_xml(self):

        output = "<serviceUnavailable xmlns=\"" + self.xml_namespace_uri + "\" code=\"" + str(self.statuscode) + "\">"

        output += "<message>" + self.message + "</message>"

        output += "</serviceUnavailable>"

        return output


    def get_as_json(self):

        output = "{" 
        output +=      "\"serviceUnavailable\"" + ":"
        output +=                "{"
        output +=                    "\"code\"" + ":" + "\"" + str(self.statuscode) + "\","
        output +=                    "\"message\"" + ":" + "\"" + self.message + "\""      
        output +=                "}"
        output += "}" 

        return output
