from openstack.lb.LoadBalancerState import LoadBalancerState

class NetScalerLoadBalancerState(LoadBalancerState): 

    def __init__(self, lbresource):
        LoadBalancerState.__init__(self, lbresource)
        self._properties += ["sureConnect", "backupState"]
        self._properties.insert(0, "xmlns")


    def validate_sureConnect_value(self):

        errors = []

        if self.sureConnect == "true":
            return True, None

        if self.sureConnect == "false":
            return True, None

        error = "sureConnect can only be set to true or false"
        errors.append(error)

        return False, errors


    def validate_backupState_value(self):
        """ XXX - No checks made """
        return True, None





