from openstack.lb.plugins.netscaler.NitroConnection import NitroConnection

class NitroConnectionManager(object):

    def __init__(self, plugin_config, logger):
        conn = NitroConnection()
        conn.host = plugin_config["host"]
        conn.port = int(plugin_config["port"])
        conn.path = plugin_config["path_prefix"]
        conn.user = plugin_config["user"]
        conn.password = plugin_config["password"] 
        conn.monitor_script = plugin_config["usermonitor_scriptfile"] 
        conn.sessionid = None

        self.connection = conn  
 

    def get_nitro_connection(self, tenant_id, loadBalancerId):

        return self.connection



