class NitroConnectionManager(object):

    def __init__(plugin_config, logger):

        host = plugin_config["host"]
        port = int(plugin_config["port"])
        path = plugin_config["path_prefix"]
        user = plugin_config["user"]
        password = plugin_config["password"] 

        """ a custom class to hold the connection settings """
        Connection = namedtuple("Connection", "host port path user password sessionid")
        self.connection = Connection(host, port, path, user, password, None)  

        nitrowrapper = NitroCallsWrapper(self.connection, logger)
 

    def get_nitro_connection(tenant_id, loadBalancerId):

        return self.connection



